window.onload = function(){
  var resize = document.getElementById("resize");
  var left = document.getElementById("left");
  var right = document.getElementById("right");
  var box = document.getElementById("box");
  resize.onmousedown = function(e){
    var startX = e.clientX;
    resize.left = resize.offsetLeft;
    document.onmousemove = function(e){
      var endX = e.clientX;

      var moveLen = resize.left + (endX - startX);

      var maxT = box.clientWidth - resize.offsetWidth;
      if(moveLen<150) moveLen = 150; 
      if(moveLen>maxT-150) moveLen = maxT-150;

      resize.style.left = moveLen;
      left.style.width = moveLen + "px";
      right.style.width = (box.clientWidth - moveLen - 5) + "px";
    }
    document.onmouseup = function(evt){
      document.onmousemove = null;
      document.onmouseup = null; 
      resize.releaseCapture && resize.releaseCapture();
    }
    resize.setCapture && resize.setCapture();
    return false;
  }
}

p1y = p2y = 40;
pt = 10;//板宽度
ph = 100;//板高度
bx = by = 50;//球横纵坐标
bd = 5;//球宽高
xv = -6;//球横方向速度
yv = 6;//球纵方向速度
// ai = 0;
pscore = ascore = 0;
dir = 1;

pv = 7;//板速度

p1Up = p1Down = p2Up = p2Down = false;

window.onload = function(){
  canv = document.getElementById("canvas");
  cc = canv.getContext("2d");
  bx = canv.width/2;
  by = Math.random()*canv.height/2;
  time = setInterval(update,1000/30);
  // while(ai<0.5 || ai>0.9){
  //   ai = Math.random().toFixed(2);
  // }
  document.addEventListener("keydown",keyDown);
  document.addEventListener("keyup",keyUp);
}
function keyDown(evt){
  switch(evt.keyCode){
    case 38:
      p2Up = true;
      break;
    case 40:
      p2Down = true;
      break;
    case 87:
      p1Up = true;
      break;
    case 83:
      p1Down = true;
      break;
  }
}
function keyUp(evt){
  switch(evt.keyCode){
    case 38:
      p2Up = false;
      break;
    case 40:
      p2Down = false;
      break;
    case 87:
      p1Up = false;
      break;
    case 83:
      p1Down = false;
      break;
  }
}
function mouseMove(evt){
  p1y = evt.clientY-ph/2;
  if(p1y<0){
    p1y = 0;
  }
  if(p1y>canv.height-ph){
    p1y = canv.height-ph;
  }
}
function reset(){
  bx = canv.width/2;
  by = Math.random()*canv.height;
  xv = -6;
  yv = 6;
  dir = 1;
  // while(ai<0.6 || ai>0.9){
  //   ai = Math.random().toFixed(2);
  // }
}
function update(){
  bx+=xv;
  by+=yv;

  if(p1Up) p1y-=pv;
  if(p1Down) p1y+=pv;
  if(p2Up) p2y-=pv;
  if(p2Down) p2y+=pv;

  if(p1y<0) p1y = 0;
  if(p2y<0) p2y = 0;
  if(p1y>canv.height-ph) p1y = canv.height-ph;
  if(p2y>canv.height-ph) p2y = canv.height-ph;

  if(dir == 1 && bx>pt && bx<pt+bd && by>p1y && by<p1y+ph-bd){
    xv = -xv;
    dir = 0;
  }
  if(dir == 0 && bx < canv.width-pt && bx>canv.width-pt-bd && by>p2y && by<p2y+ph-bd){
    xv = -xv;
    dir = 1;
  }

  if((by<0 && yv<0) || (by>canv.height-bd && yv>0)){
    yv = -yv;
  }
  if(bx>canv.width){
    pscore++;
    reset();
  }
  if(bx<0){
    ascore++;    
    reset();    
  }

  cc.fillStyle = "black";
  cc.fillRect(0,0,canv.width,canv.height);
  cc.fillStyle = "yellow";
  cc.fillRect(0,p1y,pt,ph);
  cc.fillRect(canv.width-pt,p2y,pt,ph);
  cc.fillStyle = "white";
  cc.fillRect(bx-bd/2,by,bd,bd);

  cc.font = "15px consolas";
  cc.fillStyle = "yellow";
  cc.fillText("Player:"+pscore,50,50);  
  cc.fillText("Computer:"+ascore,canv.width-130,50);  
}
